import React from "react";

class Weather extends React.Component {

	render() {

		return(

			<div> 

			{ this.props.city && this.props.country && <p>The temperature in {this.props.city}, {this.props.country} is: {this.props.temperature}</p>}

			{this.props.humidty && <p>The humidity is: {this.props.humidity}</p> }

			{ this.props.description && <p>You can expect: {this.props.description}</p> }

			{this.props.error && <p> {this.props.error} </p>}

			</div>

		);

	}

};

export default Weather;
